package com.online.test.RummyCircle;

public class SumOfIntegers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int numbers[] = { 1, 2, 3 };
		int result = 0;
		for (int i = 0; i < numbers.length; i++) {
			result += numbers[i];
		}
		System.out.println(result);

	}

}
